    private void AlogginbActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AlogginbActionPerformed

        if(Ausername.getText().trim().isEmpty() && Apass.getText().trim().isEmpty()){
         lbl_Ausername.setText("User name is Empty");
        lbl_Apassword.setText("Password is Empty");
        }
        else if(Ausername.getText().trim().isEmpty()){
            lbl_Ausername.setText("User name is Empty");
        }
        else if(Apass.getText().trim().isEmpty()){
        lbl_Apassword.setText("Password is Empty");    
        }
        else{
        String Auser,Apasss;
        Auser=Ausername.getText();
        Apasss=Apass.getSelectedText();
        if(Auser.equals("admin") || Auser.equals("Admin") && Apasss.equals("1122"))
        {
            AdminPannel ap=new AdminPannel();
            ap.setVisible(true);
        //ap.pack();
        ap.setLocationRelativeTo(null);
        ap.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        this.dispose();
        }
        else{
       System.out.println("Wrong Input");
            JOptionPane.showMessageDialog(null,"Wrong Username or Password");
        }
        
        }
    }//GEN-LAST:event_AlogginbActionPerformed

